```javascript
// script.js
const ethers = require('ethers');

// Get the user's wallet password
const walletPassword = document.getElementById('wallet-password');

// Create a wallet instance using the user's password
const wallet = ethers.Wallet.fromEncryptedJson(JSON.stringify(walletPassword.value));

// Unlock the wallet
document.getElementById('login-form').addEventListener('submit', async (e) => {
    e.preventDefault();
    try {
        // Unlock the wallet
        const unlockedWallet = await wallet.connect();

        // Get the wallet balance
        const btcBalance = await unlockedWallet.getBalance('BTC');
        const ethBalance = await unlockedWallet.getBalance('ETH');

        // Render the wallet balance
        document.getElementById('btc-balance').innerText = `${btcBalance} BTC ($${getBtcPrice() * btcBalance})`;
        document.getElementById('eth-balance').innerText = `${ethBalance} ETH ($${getEthPrice() * ethBalance})`;

        // Render the transaction history
        const transactionHistory = await unlockedWallet.getTransactionHistory();
        const transactionTableBody = document.getElementById('transaction-table-body');
        transactionHistory.forEach((transaction) => {
            const transactionRow = document.createElement('tr');
            transactionRow.classList.add('border-b', 'border-gray-300');
            transactionRow.innerHTML = `
                <td class="text-lg text-gray-800">${transaction.date}</td>
                <td class="text-lg text-gray-800">${transaction.type}</td>
                <td class="text-lg text-gray-800">${transaction.amount}</td>
            `;
            transactionTableBody.appendChild(transactionRow);
        });

        // Show the wallet balance and transaction history
        document.getElementById('wallet-balance').style.display = 'block';
        document.getElementById('transaction-history').style.display = 'block';
    } catch (error) {
        console.error(error);
    }
});

// Helper functions
function getBtcPrice() {
    // Return the current BTC price
    return 456.78;
}

function getEthPrice() {
    // Return the current ETH price
    return 2345.67;
}
```

